mapper.select:
SELECT title,price,cpdw,xpcf,hxsl,zp,localPic,xxzjbh,gzgl,jklx FROM pc_cpu WHERE gzgl is not null and price is not null and jklx is not null

	and title like CONCAT(CONCAT('%', #{param1}),'%') 


	and xpcf =#{param2}


	and jklx =#{param3}


	and hxlx =#{param4}


	and zcncpl =#{param5}
